        }
      } catch (err) {
        console.log(err);
      }
    },
    async fetchRestaurant() {
      try {
        const {
          data
        } = await axios.get(`https://c2ff7a8de0a119c8.mokky.dev/restaurant?city=${this.cityRadio2}`);
        this.restaurants = data;
      } catch (err) {
