    geocoderCoord(lat, long) {
      this.lat = lat;
      this.long = long;

      // скрытие блока при клике по карте
      this.deliveryCheck = false;
    },
    async checkAddress() {
      try {
        this.loadingDeliveryCheck = true;
        this.deliveryCheck = true;
        var polygon1 = turf.polygon([this.polygonCoord1]);
        var polygon2 = turf.polygon([this.polygonCoord2]);
        var polygon3 = turf.polygon([this.polygonCoord3]);

        // Создаем точки
        var points = turf.points([[this.lat, this.long]]);

        // Проверяем точки внутри полигона
        var pointsWithin1 = turf.pointsWithinPolygon(points, polygon1);
        var pointsWithin2 = turf.pointsWithinPolygon(points, polygon2);
        var pointsWithin3 = turf.pointsWithinPolygon(points, polygon3);
        if (pointsWithin1.features.length > 0 || pointsWithin2.features.length > 0) {
          this.deliveryPossible = true;
        } else {
          this.deliveryPossible = false;
        }
        const {
          data
        } = await axios.get(`https://geocode-maps.yandex.ru/1.x/?apikey=37720b8b-dc03-4346-b6a4-3ae3458e9af6&geocode=${this.lat},${this.long}&format=json&lang=ru_RU`);
        const path = data.response.GeoObjectCollection.featureMember[0].GeoObject.metaDataProperty.GeocoderMetaData;
        const filteredArray = path.Address.Components.filter(item => ["street", "metro", "district", "area", "province", "railway_station", "station", "vegetation", "other", "house"].includes(item.kind));
        const names = filteredArray.map(item => item.name).join(", ");
        this.addressDialog = names;
      } catch (err) {
        console.log(err);
      } finally {
        this.loadingDeliveryCheck = false;
      }
    },
    async removeAddress() {
      await axios.delete(`https://c2ff7a8de0a119c8.mokky.dev/address/${this.addressRadio}`);
      let index = this.address.findIndex(item => item.id == this.addressRadio);
      if (index !== -1) {
        this.address.splice(index, 1);
      }

      // первый адрес отмечен
      if (this.address.length > 0) {
        this.addressRadio = this.address[0].id;
      }
    },
    sumbitForm(forName) {
      forName.validate(valid => {
        if (valid) {
          this.postAddress().then(() => {
            this.addAddress = false;
            this.fetchAddress();
          });
        } else {
          return false;
        }
      });
    },
    async postAddress() {
      try {
        this.loadingPostAddress = true;
        const {
          data
        } = await axios.post(`https://c2ff7a8de0a119c8.mokky.dev/address`, {
          address: `${this.cityRadio1}, ${this.form.address}`,
          house: this.form.house,
          input: this.form.input,
          floor: this.form.floor,
          apartment: this.form.apartment
        });
        this.form.address = "";
        this.form.house = "";
        this.form.input = "";
        this.form.floor = "";
        this.form.apartment = "";
      } catch (err) {
        console.log(err);
      } finally {
        this.loadingPostAddress = false;
      }
    },
    filterArray(input, cb) {
      const results = this.restaurants.filter(element => element.address.toLowerCase().includes(input.toLowerCase()));
      cb(results);
    },
    handleSelect(item) {
      this.form.restaurant = item.address;
    },
    async validForms() {
      if (!this.ruleFormRef3) return;
      await this.ruleFormRef3.validate((valid, fields) => {
        if (valid) {
          this.validForm1 = true;
        }
      });
      if (this.tab === "tab_2") {
        await this.ruleFormRef2.validate((valid, fields) => {
          if (valid) {
            this.validForm2 = true;
          }
        });
      }
      if (this.tab === "tab_1" && (this.addAddress || this.address.length === 0)) {
        await this.ruleFormRef1.validate((valid, fields) => {
          if (valid) {
            this.validForm3 = true;
          }
        });
      }
    },
    generateTimeOptions() {
      this.optionsTime = [];
      if (this.selectDay === this.optionsDay[0].value) {
        let currentDate = new Date();
        let hours = currentDate.getHours();
        let minutes = currentDate.getMinutes();
        this.optionsTime.push({
          value: `${hours}:${minutes}`,
          label: "Ближайшее"
        });
        if (minutes > 30) {
          this.optionsTime.push({
            value: `${hours + 2}:30`,
            label: `${hours + 2}:30`
          });
          for (let i = hours + 3; i < 23; i++) {
            this.optionsTime.push({
              value: `${i}:00`,
              label: `${i}:00`
            });
            this.optionsTime.push({
              value: `${i}:30`,
              label: `${i}:30`
            });
          }
        } else {
          for (let i = hours + 2; i < 23; i++) {
            this.optionsTime.push({
              value: `${i}:00`,
              label: `${i}:00`
            });
            this.optionsTime.push({
              value: `${i}:30`,
              label: `${i}:30`
            });
          }
        }
      } else {
        for (let i = 11; i < 23; i++) {
          this.optionsTime.push({
            value: `${i}:00`,
            label: `${i}:00`
          });
          this.optionsTime.push({
            value: `${i}:30`,
            label: `${i}:30`
          });
        }
      }
      this.selectTime = this.optionsTime[0].value;
    },
    postOrder() {
      this.validForms().then(() => {
        if (this.tab === "tab_2") {
          //самовывоз
          if (this.validForm1 && this.validForm2) {
            this.createOrder();
          }
        } else if (this.tab === "tab_1" && (this.addAddress || this.address.length === 0)) {
          // доставка и добавить
          if (this.validForm1 && this.validForm3) {
            this.createOrder();
          }
        } else {
          // доставка и сохраненный адрес
          if (this.validForm1) {
            this.createOrder();
          }
        }
      });
    },
    async createOrder() {
      try {
        this.isCreatingOrder = true;
        const info = {
          name: this.form.name,
          tel: this.form.tel,
          day: this.selectDay,
          time: this.selectTime,
          payment: this.tab === "tab_1" ? this.paymentDelivery : this.paymentPickup,
          items: this.cart,
          totalPrice: this.totalPrice,
          animals: this.animals,
          methods: this.tab === "tab_1" ? "Доставка" : "Самовывоз",
          address: "",
          options: ""
        };

        // сдача
        if (this.paymentDelivery === "Наличными курьеру" && this.tab === "tab_1" || this.paymentPickup === "Наличными в точке выдачи" && this.tab === "tab_2") {
          info.change = this.form.change ? this.form.change : "без сдачи";
        }
        if (this.form.comment1) {
          info.regularSticks = this.form.comment1;
        }
        if (this.form.comment2) {
          info.traningSticks = this.form.comment2;
        }
        if (this.form.note) {
          info.note = this.form.note;
        }
        if (this.freeProduct.length > 0) {
          info.freeProduct = this.freeProduct;
        }
        if (this.tab === "tab_1" && !this.addAddress && this.address.length > 0) {
          const result = this.address.find(item => {
            return item.id === this.addressRadio;
          });
          info.address = result;
          info.options = this.doorOptions1;
        }
        if (this.tab === "tab_1" && (this.addAddress || this.address.length === 0)) {
          const result = {
            address: `${this.cityRadio1}, ${this.form.address}`,
            house: this.form.house,
            input: this.form.input,
            floor: this.form.floor,
            apartment: this.form.apartment
          };
          info.address = result;
          info.options = this.doorOptions2;
        }
        if (this.tab === "tab_2") {
          info.address = `${this.cityRadio2}, ${this.form.restaurant}`;
        }
        const {
          data
        } = await axios.post(`https://c2ff7a8de0a119c8.mokky.dev/orders`, info);
        this.cart = [];
        this.orderId = data.id;

        // история заказов в localStorage
        this.historyOrder.push(this.orderId);
        localStorage.setItem("order", JSON.stringify(this.historyOrder));
      } catch (err) {
        console.log(err);
      } finally {
        this.isCreatingOrder = false;
        this.resetForm();
      }
    },
