    const ruleFormRef6 = ref({});
    const ruleFormRef7 = ref({});
    const ruleFormRef8 = ref({});
    const code = ref();
    const codeValid = ref();
    const codeLoading = ref(false);
    const freeProduct = ref([]);
    const freeLoading = ref(false);

    // кастомное правило
    const checkNumber = (rule, value, callback) => {
      if (!Number.isInteger(value) && value) {
        callback(new Error("Введите число!"));
      } else {
        if (value <= 0 && value) {
          callback(new Error("Неверное кол-во!"));
        } else {
          callback();
        }
      }
    };

    // получение промокода
    const fetchCode = async () => {
      try {
        codeLoading.value = true;
        const {
          data
        } = await axios.get(`https://c2ff7a8de0a119c8.mokky.dev/code`);
        code.value = data[0].code;
      } catch (err) {
        console.log(err);
      } finally {
        codeLoading.value = false;
      }
    };

    // получение товаров по промокоду
    const fetchFree = async () => {
      try {
        freeLoading.value = true;
        const {
          data
        } = await axios.get(`https://c2ff7a8de0a119c8.mokky.dev/items?category=snacks&id=41`);
        freeProduct.value = data.map(obj => ({
          ...obj,
          count: 1
        }));
      } catch (err) {
        console.log(err);
      } finally {
        freeLoading.value = false;
      }
    };
    const checkCode = () => {
      fetchCode().then(() => {
        if (code.value === form.value.code) {
          codeValid.value = true;
          fetchFree();
        } else {
          codeValid.value = false;
        }
      });
    };
    const cancelCode = () => {
      freeProduct.value = [];
      codeValid.value = "";
      form.value.code = "";
    };
    const rules = ref({
      address: [{
        required: true,
        message: "Нужно заполнить!",
        trigger: "blur"
      }, {
        min: 5,
        message: "Слишком короткий!",
        trigger: "blur"
      }],
      name: [{
        required: true,
        message: "Нужно заполнить!",
        trigger: "blur"
      }, {
        min: 3,
        message: "Слишком короткий!",
        trigger: "blur"
      }],
      tel: [{
        required: true,
        message: "Нужно заполнить!",
        trigger: "blur"
      }, {
        min: 18,
        message: "Слишком короткий!",
        trigger: "blur"
      }],
      number: [{
        validator: checkNumber,
        trigger: "blur"
      }]
    });
    const onClickAddPlus = item => {
      if (!item.isAdded) {
        addToCart(item);
      } else {
        removeFromCart(item);
      }
    };
    return {
      address,
      form,
      ruleFormRef1,
      ruleFormRef2,
      ruleFormRef3,
      ruleFormRef4,
      ruleFormRef5,
      ruleFormRef6,
      ruleFormRef7,
      ruleFormRef8,
      polygonCoord1,
      polygonCoord2,
      polygonCoord3,
      coord,
      rules,
      cart,
      onClickAddPlus,
      products,
      totalPrice,
      checkCode,
      codeValid,
      codeLoading,
      freeProduct,
      cancelCode,
      freeLoading,
      historyOrder
    };
  }
