        label: "Без разницы"
      }],
      selectDay: "",
      selectTime: "",
      deliveryPossible: false,
      deliveryCheck: false,
      addressDialog: "",
      lat: "",
      long: "",
      loadingDeliveryCheck: false,
      svg: `
        <path class="path" d="
          M 30 15
          L 28 17
          M 25.61 25.61
          A 15 15, 0, 0, 1, 15 30
          A 15 15, 0, 1, 1, 27.99 7.5
          L 15 15
        " style="stroke-width: 4px; fill: rgba(0, 0, 0, 0); stroke: #f63"/>
      `,
      paymentDelivery: "radio-1",
      paymentPickup: "radio-1",
      sliderLoading: false
    };
  },
  methods: {
    async fetchAddress() {
      try {
        const {
          data
        } = await axios.get(`https://c2ff7a8de0a119c8.mokky.dev/address`);
        this.address = data;
      } catch (err) {
        console.log(err);
      }
    },
    async fetchRestaurant() {
      try {
        var city;
        const radioButtons = document.getElementsByName("city");
        radioButtons.forEach((item, index) => {
          if (item.checked) {
            city = item.nextElementSibling.innerText;
          }
        });
        const {
          data
        } = await axios.get(`https://c2ff7a8de0a119c8.mokky.dev/restaurant?city=${city}`);
        this.restaurants = data;
      } catch (err) {
        console.log(err);
      }
    },
    async fetchProduct() {
      try {
        this.sliderLoading = true;
        const {
          data
        } = await axios.get(`https://c2ff7a8de0a119c8.mokky.dev/items?category=drinks`);
        this.products = data.map(obj => ({
          ...obj,
          isAdded: false,
          count: 1
        }));
      } catch (err) {
        console.log(err);
      } finally {
        this.sliderLoading = false;
      }
    },
    geocoderCoord(lat, long) {
      this.lat = lat;
      this.long = long;

      // скрытие блока при клике по карте
      this.deliveryCheck = false;
    },
    async checkAddress() {
      try {
        this.loadingDeliveryCheck = true;
        this.deliveryCheck = true;
        var polygon1 = turf.polygon([this.polygonCoord1]);
        var polygon2 = turf.polygon([this.polygonCoord2]);
        var polygon3 = turf.polygon([this.polygonCoord3]);

        // Создаем точки
        var points = turf.points([[this.lat, this.long]]);

        // Проверяем точки внутри полигона
        var pointsWithin1 = turf.pointsWithinPolygon(points, polygon1);
        var pointsWithin2 = turf.pointsWithinPolygon(points, polygon2);
        var pointsWithin3 = turf.pointsWithinPolygon(points, polygon3);
        if (pointsWithin1.features.length > 0 || pointsWithin2.features.length > 0) {
          this.deliveryPossible = true;
        } else {
          this.deliveryPossible = false;
        }
        const {
          data
        } = await axios.get(`https://geocode-maps.yandex.ru/1.x/?apikey=37720b8b-dc03-4346-b6a4-3ae3458e9af6&geocode=${this.lat},${this.long}&format=json&lang=ru_RU`);
        const path = data.response.GeoObjectCollection.featureMember[0].GeoObject.metaDataProperty.GeocoderMetaData;
        const filteredArray = path.Address.Components.filter(item => ["street", "metro", "district", "area", "province", "railway_station", "station", "vegetation", "other", "house"].includes(item.kind));
        const names = filteredArray.map(item => item.name).join(", ");
        this.addressDialog = names;
      } catch (err) {
        console.log(err);
      } finally {
        this.loadingDeliveryCheck = false;
      }
    },
    async removeAddress() {
      var id;
      const radioButtons = document.getElementsByName("address");
      radioButtons.forEach((item, index) => {
        if (item.checked) {
          id = item.getAttribute("label-id");
        }
      });
      radioButtons[0].checked = true;
      await axios.delete(`https://c2ff7a8de0a119c8.mokky.dev/address/${id}`);
      let index = this.address.findIndex(item => item.id == id);
      if (index !== -1) {
        this.address.splice(index, 1);
      }
    },
    sumbitForm(forName) {
      forName.validate(valid => {
        if (valid) {
          this.postAddress().then(() => {
            this.addAddress = false;
            this.fetchAddress();
          });
        } else {
          return false;
        }
      });
    },
    async postAddress() {
      try {
        this.loadingPostAddress = true;
        var city;
        const radioButtons = document.getElementsByName("city");
        radioButtons.forEach((item, index) => {
          if (item.checked) {
            city = item.nextElementSibling.innerText;
          }
        });
        const {
          data
        } = await axios.post(`https://c2ff7a8de0a119c8.mokky.dev/address`, {
          address: `${city}, ${this.form.address}`,
          house: this.form.house,
          input: this.form.input,
          floor: this.form.floor,
          apartment: this.form.apartment
        });
        this.form.address = "";
        this.form.house = "";
        this.form.input = "";
        this.form.floor = "";
        this.form.apartment = "";
      } catch (err) {
        console.log(err);
      } finally {
        this.loadingPostAddress = false;
      }
    },
    filterArray(input, cb) {
      const results = this.restaurants.filter(element => element.address.toLowerCase().includes(input.toLowerCase()));
      cb(results);
    },
    handleSelect(item) {
      this.form.restaurant = item.address;
    }
  },
  mounted() {
    this.fetchAddress();
    // значения по умолчанию
    this.selectDay = this.optionsDay[0].value;
    this.selectTime = this.optionsTime[0].value;
    // координаты центра по умолчанию
    this.lat = this.coord[0];
    this.long = this.coord[1];
    // slider
    this.fetchProduct().then(() => {
      this.products = this.products.map(item => ({
        ...item,
        isAdded: this.cart.some(cartItem => cartItem.id === item.id)
      }));
    });
  },
  setup() {
    const {
      polygonCoord1,
      polygonCoord2,
      polygonCoord3
    } = inject("address");
    const {
      address,
      coord
    } = inject("address");
    const {
      cart,
      addToCart,
      removeFromCart
    } = inject("cart");
    const {
      products
    } = inject("items");
    const form = ref({
      name: "",
      tel: "",
      address: "",
      house: "",
      input: "",
      floor: "",
      apartment: "",
      restaurant: "",
      comment1: "",
      comment2: "",
      note: "",
      change: ""
    });
    const ruleFormRef1 = ref();
    const ruleFormRef2 = ref();
    const ruleFormRef3 = ref();
    const ruleFormRef4 = ref();
    const ruleFormRef5 = ref();
    const checkNumber = (rule, value, callback) => {
      if (!Number.isInteger(value) && value) {
        callback(new Error("Введите число!"));
      } else {
        if (value <= 0 && value) {
          callback(new Error("Неверное кол-во!"));
        } else {
          callback();
        }
      }
    };
    const onClickAddPlus = item => {
      if (!item.isAdded) {
        addToCart(item);
      } else {
        removeFromCart(item);
      }
    };
    const rules = ref({
      address: [{
        required: true,
        message: "Нужно заполнить!",
        trigger: "blur"
      }, {
        min: 5,
        message: "Слишком короткий!",
        trigger: "blur"
      }],
      name: [{
        required: true,
        message: "Нужно заполнить!",
        trigger: "blur"
      }, {
        min: 3,
        message: "Слишком короткий!",
        trigger: "blur"
      }],
      tel: [{
        required: true,
        message: "Нужно заполнить!",
        trigger: "blur"
      }, {
        min: 18,
        message: "Слишком короткий!",
        trigger: "blur"
      }],
      number: [{
        validator: checkNumber,
        trigger: "blur"
      }]
    });
    return {
      address,
      form,
      ruleFormRef1,
      ruleFormRef2,
      ruleFormRef3,
      ruleFormRef4,
      ruleFormRef5,
      polygonCoord1,
      polygonCoord2,
      polygonCoord3,
      coord,
      rules,
      cart,
      onClickAddPlus,
      products
    };
  }
};
