import { watch } from "vue";

export default {
  mounted(el, binding) {
    const spinner = document.createElement('div');
    spinner.className = 'loader-box';
    spinner.style = 'position: absolute; inset: 0;'
    spinner.innerHTML = '<div class="loader"></div>'
    el.appendChild(spinner);
    console.log()
    el.style.position = 'relative';
    if (binding.value) {
      spinner.style.display = 'flex';
    } else {
      spinner.style.display = 'none';
    }
  },
  updated(el, binding) {
    const spinner = el.querySelector('.loader-box');
    if (binding.value) {
      spinner.style.display = 'flex';
    } else {
      spinner.style.display = 'none';
    }
  },
  name: 'load'
}
