export default {
  mounted(el, binding) {
    const spinner = document.createElement('div');
    spinner.innerHTML = ''
    spinner.className = 'loader ';
    el.appendChild(spinner);
    if (binding.value) {
      spinner.style.display = 'block';
    }
  },
  name: 'load'
}
