export default {
  mounted(el, binding) {
    const spinner = document.createElement('div');
    spinner.className = ''
    spinner.innerHTML = '<div class="loader"></div>'
    el.appendChild(spinner);
    if (binding.value) {
      spinner.style.display = 'block';
    }
  },
  name: 'load'
}
