import { watch } from "vue";

export default {
  mounted(el, binding) {
    this.spinner = document.createElement('div');
    this.spinner.className = 'loader-box';
    this.spinner.style = 'position: absolute; inset: 0;'
    this.spinner.innerHTML = '<div class="loader"></div>'
    el.appendChild(this.spinner);
    console.log(binding)
    el.style.position = 'relative';
    if (binding.value) {
      this.spinner.style.display = 'flex';
    } else {
      this.spinner.style.display = 'none';
    }
  },
  updated(el, binding) {
    if (binding.value) {
      this.spinner.style.display = 'flex';
    } else {
      this.spinner.style.display = 'none';
    }
  },
  name: 'load'
}
