import axios from "axios";
import * as turf from "@turf/turf";

export const mapCheckModule = {
  state: () => ({
    deliveryPossible: false
  }),
  mutations: {
    setDeliveryPossible(state, deliveryPossible) {
      state.deliveryPossible = deliveryPossible;
    }
  },
  actions: {
    async checkAddress(polygonCoord1, polygonCoord2, polygonCoord3, lat, long) {
      try {
        var polygon1 = turf.polygon([polygonCoord1]);
        var polygon2 = turf.polygon([polygonCoord2]);
        var polygon3 = turf.polygon([polygonCoord3]);

        // Создаем точки
        var points = turf.points([[lat, long]]);

        // Проверяем точки внутри полигона
        var pointsWithin1 = turf.pointsWithinPolygon(points, polygon1);
        var pointsWithin2 = turf.pointsWithinPolygon(points, polygon2);
        var pointsWithin3 = turf.pointsWithinPolygon(points, polygon3);

        if (pointsWithin1.features.length > 0 || pointsWithin2.features.length > 0) {
          state.deliveryPossible = true;
          commit('setDeliveryPossible', state.deliveryPossible)
        } else {
          state.deliveryPossible = false;   
        }

        const { data } = await axios.get(
          `https://geocode-maps.yandex.ru/1.x/?apikey=37720b8b-dc03-4346-b6a4-3ae3458e9af6&geocode=${this.lat},${this.long}&format=json&lang=ru_RU`
        );

        const path =
          data.response.GeoObjectCollection.featureMember[0].GeoObject.metaDataProperty
            .GeocoderMetaData;
        const filteredArray = path.Address.Components.filter((item) =>
          [
            "street",
            "metro",
            "district",
            "area",
            "province",
            "railway_station",
            "station",
            "vegetation",
            "other",
            "house",
          ].includes(item.kind)
        );
        const names = filteredArray.map((item) => item.name).join(", ");
        this.addressDialog = names;
      } catch (err) {
        console.log(err);
      }
    },
  },
  namespaced: true
}
